catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading) {
		return;
	}

	var building = g_form.getValue('building_name');

	if (building) {
		g_form.setReadOnly('floor', false);
		g_form.setReadOnly('space', false);
		g_form.setReadOnly('country', true);
		g_form.setReadOnly('state_province', true);
		g_form.setReadOnly('city', true);

		if (!g_form.getValue('state_province') || !g_form.getValue('city') || !g_form.getValue('country')) {
			var ga = new GlideAjax('CIRPSIAjaxUtils');
			ga.addParam('sysparm_name', 'getBuildingData');
			ga.addParam('sysparm_building_name', g_form.getValue('building_name'));
			ga.addParam('sysparm_country', g_form.getValue('country'));
			//ga.addParam('sysparm_state', g_form.getValue('state_province'));
			//ga.addParam('sysparm_city', g_form.getValue('city'));
			//ga.addParam('sysparm_fromRp', true);
			ga.getXML(getData);
		}
	}

	if (!building) {
		if (!g_user.hasRole('x_fru_cir.psi_internal_user')) {
			g_form.setReadOnly('country', false);
		}
		g_form.setReadOnly('state_province', false);
		g_form.setReadOnly('city', false);
		g_form.setReadOnly('floor', true);
		g_form.setReadOnly('space', true);
		g_form.setValue('floor','');
	}

	function getData(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var address = JSON.parse(answer);

		if (address.country) {
			g_form.setValue('country', address.country);
		} else { g_form.setValue('country', '');}

		if (address.state) {
			g_form.setValue('state_province', address.state);
		} else {g_form.setValue('state_province', '');}

		if (address.city){
			g_form.setValue('city', address.city);
		} else { g_form.setValue('city', '');}
	}
}
