catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue === '') {
		g_form.clearValue('title');
		g_form.clearValue('user_id');
		g_form.clearValue('phone_number');
		g_form.clearValue('email');
		g_form.clearValue('company');
		return;
	}

	var receiverInfo = new GlideAjax('CIRPSIAjaxUtils');
	receiverInfo.addParam('sysparm_name','getUserInfo');
	// send the user sys_id to the server
	receiverInfo.addParam('sysparm_user', newValue);
	receiverInfo.getXMLAnswer(handleResult);

	function handleResult(response) {
		var user = JSON.parse(response);
		if (user.phone) {
			g_form.setValue('phone_number', user.phone);
		} else {
			g_form.setValue('phone_number', "");
		}
		g_form.setValue('email', user.email);
		g_form.setValue('company', user.companydv);
		g_form.setValue('user_id', user.user_name);
		g_form.setValue('title', user.title);
	}
}
