catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    g_form.clearMessages();

    var startDate = g_form.getValue('occurred_on');
    var endDate = g_form.getValue('end_time');

    var ga = new GlideAjax('CIRDateUtilsAjax');
    ga.addParam("sysparm_name", 'validateDates');
    ga.addParam("sysparm_start_date", startDate);
    ga.addParam("sysparm_end_date", endDate);
    ga.addParam("sysparm_validate_past", 'false');
    ga.getXML(function(response) {
        var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
        if (!answer) {
            g_form.addInfoMessage('The selected Start time is after the End time. Please add an Start time before End time.');
            g_form.setValue('occurred_on', '');
        }
    });
}
