catalog_script_client.script:
function onLoad() {
	g_form.setValue('country', getParameterValue('country'));
	g_form.setValue('state_province', getParameterValue('state'));
	g_form.setValue('city', getParameterValue('city'));
	g_form.setValue('building_name', getParameterValue('building'));
	g_form.setValue('floor', getParameterValue('floor'));

	if (!g_form.getValue('country')) {
		var ga = new GlideAjax('CIRPSIAjaxUtils');
		ga.addParam('sysparm_name', 'getUserCountryValue');
		ga.getXML(getData);
	}
}

function getData(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('country', answer);
}

function getParameterValue(name) {
	name = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
	var regexS = "[\\?&]" + name + "=([^&#]*)";
	var regex = new RegExp(regexS);
	var results = regex.exec(top.location);
	if (results === null) {
		return "";
	} else {
		return unescape(results[1]);
	}
}
