sp_rectangle_menu_item.record_script:
data.items = [];
data.count = 0;

// use record watchers to tell header when to update dropdown counts
data.record_watchers = [];

var queryString = new CIRCaseManagementUtilsFP().GetMyCasesQuery();
data.record_watchers.push({'table': CIRConstants.Tables.CASE,'filter':queryString});

var caseCount = new GlideAggregate(CIRConstants.Tables.CASE);
caseCount.addAggregate('COUNT');
caseCount.addEncodedQuery(queryString);
caseCount.query();
if(caseCount.next()){
  data.count = caseCount.getAggregate('COUNT');
}

var cases = new GlideRecord(CIRConstants.Tables.CASE);
cases.addEncodedQuery(queryString);
cases.orderByDesc('sys_updated_on');
cases.setLimit(5);
cases.query();
while (cases.next()) {
  var item = {};
  item.short_description = cases.short_description + "";
  $sp.getRecordValues(item, cases, 'sys_id,sys_updated_on');
  item.number = cases.getDisplayValue();
  item.__table = cases.getRecordClassName();
  item.__page = 'cir_ciri_form';

  item.type = 'record';
  data.items.push(item);
}

var link = {};
link.title = gs.getMessage('View all cases');
link.type = 'link';
link.href = '?id=cir_ciri_list&table=' + CIRConstants.Tables.CASE + '&filter=' + queryString;
link.items = [];
data.items.push(link); // put this last
