sp_rectangle_menu_item.record_script:
data.items = [];
data.count = 0;

// use record watchers to tell header when to update dropdown counts
data.record_watchers = [];

var queryString = new CIRRiskManagementUtilsFP().GetInvestigationsQuery();
data.record_watchers.push({
    'table': CIRConstants.Tables.INVESTIGATION,
    'filter': queryString
});

var investigationCount = new GlideAggregate(CIRConstants.Tables.INVESTIGATION);
investigationCount.addAggregate('COUNT');
investigationCount.addEncodedQuery(queryString);
investigationCount.query();
if(investigationCount.next()) {
    data.count = investigationCount.getAggregate('COUNT');
}

var investigations = new GlideRecord(CIRConstants.Tables.INVESTIGATION);
investigations.addEncodedQuery(queryString);
investigations.orderByDesc('sys_updated_on');
investigations.setLimit(5);
investigations.query();
while(investigations.next()) {
    var item = {};
    item.short_description = investigations.short_description + "";
    $sp.getRecordValues(item, investigations, 'sys_id,sys_updated_on');
    item.number = investigations.getDisplayValue();
    item.__table = investigations.getRecordClassName();
    item.__page = 'cir_ciri_form';

    item.type = 'record';
    data.items.push(item);
}

var link = {};
link.title = gs.getMessage('View all investigations');
link.type = 'link';
link.href = '?id=cir_ciri_list&table=' + CIRConstants.Tables.INVESTIGATION + '&filter=' + queryString;
link.items = [];
data.items.push(link); // put this last
