sp_rectangle_menu_item.record_script:
data.items = [];
data.count = 0;

// use record watchers to tell header when to update dropdown counts
data.record_watchers = [];

var queryString = new CIRRiskManagementUtilsFP().GetMyActionsQuery();
data.record_watchers.push({'table': CIRConstants.Tables.ACTION,'filter':queryString});

var actionCount = new GlideAggregate(CIRConstants.Tables.ACTION);
actionCount.addAggregate('COUNT');
actionCount.addEncodedQuery(queryString);
actionCount.query();
if(actionCount.next()){
  data.count = actionCount.getAggregate('COUNT');
}

var actions = new GlideRecord(CIRConstants.Tables.ACTION);
actions.addEncodedQuery(queryString);
actions.orderByDesc('sys_updated_on');
actions.setLimit(5);
actions.query();
while (actions.next()) {
  var item = {};
  item.short_description = actions.short_description + "";
  $sp.getRecordValues(item, actions, 'sys_id,sys_updated_on');
  item.number = actions.getDisplayValue();
  item.__table = actions.getRecordClassName();
  item.__page = 'cir_ciri_form';

  item.type = 'record';
  data.items.push(item);
}

var link = {};
link.title = gs.getMessage('View all actions');
link.type = 'link';
link.href = '?id=cir_ciri_list&table=' + CIRConstants.Tables.ACTION + '&view=sp&filter=' + queryString;
link.items = [];
data.items.push(link); // put this last
