sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
}

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.investigator = gs.hasRole('x_fru_cir.investigator');
    data.investigatorFilter = new CIRRiskManagementUtils().GetInvestigationsQuery();
    data.caseFilter = new CIRCaseManagementUtils().getMyCasesQuery();
    data.actionsFilter = new CIRRiskManagementUtils().GetMyActionsQuery();

    var actionCount = new GlideAggregate(CIRConstants.Tables.ACTION);
    actionCount.addAggregate('COUNT');
    actionCount.addEncodedQuery(data.actionsFilter);
    actionCount.query();
    if(actionCount.next()) {
        data.actionCount = actionCount.getAggregate('COUNT');
    }

    var caseCount = new GlideAggregate(CIRConstants.Tables.CASE);
    caseCount.addAggregate('COUNT');
    caseCount.addEncodedQuery(data.caseFilter);
    caseCount.query();
    if(caseCount.next()) {
        data.caseCount = caseCount.getAggregate('COUNT');
    }

    var investigationCount = new GlideAggregate(CIRConstants.Tables.INVESTIGATION);
    investigationCount.addAggregate('COUNT');
    investigationCount.addEncodedQuery(data.investigatorFilter);
    investigationCount.query();
    if(investigationCount.next()) {
        data.investigationCount = investigationCount.getAggregate('COUNT');
    }


    data.reportACaseSysId = gs.getProperty('x_fru_cir.sc_cat_item.report_a_case');
})();
