sp_widget.client_script:
function($scope, $rootScope, $http) {
    /* widget controller */
    var c = this;
    $rootScope.updateGrid = function() {
        var filter = $rootScope.locationFilter;
        var queryString = "/api/x_fru_cir/portal/getGridStatus?locationTypes=" + filter.locationTypes +
            '&caseTypes=' + filter.caseTypes + '&favorites_only=' + filter.favorites_only + '&searchText=' + filter
            .searchText;

        var req = {
            method: 'GET',
            url: queryString,
            headers: {
                "X-UserToken": window.g_ck,
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        };

        $http(req).then(function successCallback(response) {
            if(response) {
                var gridObjects = response.data.result;
                for(var attribute in gridObjects) {
                    if(gridObjects.hasOwnProperty(attribute)) {
                        $scope.data.gridStatus[attribute] = gridObjects[attribute];
                    }
                }
            }
        }, function errorCallback(response) {
            var logMessage = gs.getMessage('grid_status_lookup_via_rest', response);
            jslog(logMessage);

            //jslog('Error with Grid Status lookup via REST: ' + response);
        });
    };
}

sp_widget.css:
.gridEntry{
  border: solid gray;
  padding: 20px;
  text-align: center;
  color: #bdc0c4;
}
.gridCount{
  color:white;
}

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    // Get the field labels from sys_documentation table

    var nearMissTableName = CIRConstants.Tables.NEAR_MISS;
    var incidentName = CIRConstants.Tables.INCIDENT;
    var hazardTableName = CIRConstants.Tables.HAZARD;
    var element = '';

    var cirCaseManagementUtils = new CIRCaseManagementUtilsFP();

    var nearMissGR = cirCaseManagementUtils.GetFieldLabels(nearMissTableName, element);
    var incGR = cirCaseManagementUtils.GetFieldLabels(incidentName, element);
    var hazardGR = cirCaseManagementUtils.GetFieldLabels(hazardTableName, element);

    data.nearMissLabel = nearMissGR.plural.toString();
    data.incLabel = incGR.plural.toString();
    data.hazardLabel = hazardGR.plural.toString();

    data.gridStatus = cirCaseManagementUtils.GetGridStatus(cirCaseManagementUtils.GetLocationsFromFilter());
})();
