sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
}

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    var year = new GlideDate();
    data.currentYear = year.getByFormat("yy").split('');

    data.deathTotal = 0;
    data.lostTimeCaseTotal = 0;
    data.restrictedCaseTotal = 0;
    data.otherTotal = 0;
    data.lostTimeTotal = 0;
    data.restrictedTotal = 0;

    data.injuryTotal = 0;
    data.respiratoryTotal = 0;
    data.skinTotal = 0;
    data.hearingTotal = 0;
    data.poisonTotal = 0;
    data.otherIllnessTotal = 0;

    var query = $sp.getParameter('ids') || options.ids;
    var page = $sp.getParameter('page') || options.page || '1,1';
    if(page)
        page = page.split(',');

    //data.casePeopleArr = [];
    data.people = [];
    data.totalPages = page[1];
    data.currentPage = page[0];

    //Must be glide record due to security that we have to bypass only for these OSHA forms
    var impactedPersons = new GlideRecordSecure(CIRConstants.Tables.PERSON_IMPACT);
    if(query)
        impactedPersons.addQuery('sys_id', 'IN', query);
    else
        impactedPersons.addEncodedQuery(
            'cir_case.occurred_onONlastyear@javascript:gs.beginningOfLastYear()@javascript:gs.endOfLastYear()');
    impactedPersons.orderBy('cir_case.number');
    impactedPersons.setLimit(13);
    impactedPersons.query();
    while(impactedPersons.next()) {
        var grCase = impactedPersons.getElement('cir_case').getRefRecord();
        var location = grCase.location.nil() ? grCase.location_notes
            .toString() : grCase.location.getDisplayValue();
        var number = grCase.number.toString();
        number = number.replace('CIR', '');
        number = parseInt(number, 10);

        var occurredOn = new GlideDate();
        occurredOn.setValue(grCase.occurred_on.toString());

        var injuryDescription = '';
        var injuryIllnessTypes = [];
        var count = 0;
        //Must be glide record due to security that we have to bypass only for these OSHA forms
        var injuries = new GlideRecordSecure(CIRConstants.Tables.INJURY_ILLNESS);
        injuries.addQuery('impacted_person', impactedPersons.sys_id.toString());
        injuries.query();
        while(injuries.next()) {
            if(count == 0) {
                injuryDescription = injuries.display_name.toString();
                count++;
            } else {
                injuryDescription = '';
            }

            if(injuries.type == 'injury' && injuryIllnessTypes.indexOf('injury') == -1) {
                injuryIllnessTypes.push('injury');
                data.injuryTotal++;
            } else if(injuries.type == 'illness' && injuries.subcategory == 'respiratory_system') {
                injuryIllnessTypes.push('respiratory');
                data.respiratoryTotal++;
            } else if(injuries.type == 'illness' && injuries.subcategory == 'skin_subcutaneous_tissue') {
                injuryIllnessTypes.push('skin');
                data.skinTotal++;
            } else if(injuries.type == 'illness' && injuries.nature_of_illness == 'hearing_loss') {
                injuryIllnessTypes.push('hearing');
                data.hearingTotal++;
            } else if(injuries.type == 'illness' && injuries.nature_of_illness == 'bacterial_food_poisoning') {
                injuryIllnessTypes.push('poison');
                data.poisonTotal++;
            } else {
                injuryIllnessTypes.push('other');
                data.otherIllnessTotal++;
            }
        }
        var caseClassification = impactedPersons.job_impact.toString();
        var timeLostRestricted = new GlideDateTime(impactedPersons.time_lost_restricted.toString());
        var timeLost = 0;
        var timeRestricted = 0;
        if(caseClassification == 'lost_time') {
            timeLost = Math.floor((timeLostRestricted.getNumericValue() / 1000) / 86400);
            data.lostTimeTotal += timeLost;
            data.lostTimeCaseTotal++;
        } else if(caseClassification == 'restricted_duties') {
            timeRestricted = Math.floor((timeLostRestricted.getNumericValue() / 1000) / 86400);
            data.restrictedTotal += timeRestricted;
            data.restrictedCaseTotal++;
        } else if(caseClassification == 'deceased') {
            data.deathTotal++;
        } else {
            caseClassification = 'other';
            data.otherTotal++;
        }

        data.people.push({
            number: number,
            employeeName: impactedPersons.first_name + ' ' + impactedPersons.last_name,
            jobTitle: impactedPersons.job_title.toString(),
            occurredOn: occurredOn.getByFormat('MM-dd').split('-'),
            location: location,
            injuries: injuryDescription,
            injuryTypes: injuryIllnessTypes.join(','),
            classification: caseClassification,
            timeLost: timeLost,
            timeRestricted: timeRestricted
        });

    }
    while(data.people.length < 13) {
        data.people.push({
            number: '',
            employeeName: '',
            jobTitle: '',
            occurredOn: '',
            location: '',
            injuries: '',
            injuryTypes: '',
            classification: '',
            timeLost: '',
            timeRestricted: ''
        });
    }
})();
