sp_widget.client_script:
function($scope, $rootScope, spUtil, $location) {
    var c = this;
    c.expanded = !spUtil.isMobile();
    c.expand = function() {
        c.expanded = true;
    };

    var deregister = $rootScope.$on("sp.update.breadcrumbs", function(e, list) {
        c.breadcrumbs = list;
    });

    $rootScope.$on("sp.fix.breadcrumbs", function(e, index) {
        $scope.fixBreadcrumbs(index + 1);
    });

    $scope.$on('$destroy', function() {
        deregister();
    });

    $scope.fixBreadcrumbs = function(index) {
        $rootScope.cirBreadcrumbsForForm = $rootScope.cirBreadcrumbsForForm.slice(0, index);
    };

}

sp_widget.css:
.nav {
  li {
    padding: 10px 7px;
    font-size: $breadcrumbs-font-size;

    > span {
      > a {
          display: inline;
      }

      > i {
          padding-left: 15px;
      }
    }
  }
}

.a-disabled {
  cursor: default;
  color: $sp-disabled-breadcrumb;
}
