sp_widget.script:
(function () {
    data.tables = CIRConstants.Tables;
    data.show_more = false;
    data.show_popular_item = false;
    data.items = [];
    var itemsInPage = options.limit_item || 9;
    data.create_crisis = input ? input.create_crisis : false;
    data.category_clicked = input ? input.category_clicked : '';

    if (data.create_crisis) {
        data.created_record = createCrisis(data.category_clicked);
    } else {
        data.limit = itemsInPage;
        if (input && input.new_limit)
            data.limit = input.new_limit;
        if (input && input.items) {
            data.items = input.items.slice();
        }

        if (input && input.startWindow) {
            data.endWindow = input.endWindow;
        } else {
            data.startWindow = 0;
            data.endWindow = 0;
        }

        while (data.items.length < data.limit + 1) {
            data.startWindow = data.endWindow;
            data.endWindow = data.endWindow + itemsInPage;

            var itemGR = queryItems(data.startWindow, data.endWindow);
            if (!itemGR.hasNext())
                break;
            fetchItemDetails(itemGR, data.items);
        }

        if (data.items.length > data.limit) {
            data.show_more = true;
        }
        var numbers = Math.round(data.limit);
        if (data.items.indexOf(data.placeholderItem)) {
            numbers = numbers - 1;
        }

        data.more_msg = gs.getMessage(" Showing {0} items", data.limit);
    }

    function createCrisis(category) {
        var grCrisis = new GlideRecord(data.tables.SITUATION);
        grCrisis.initialize();
        grCrisis.category = category.toString();

        return grCrisis.insert();
    }

    function fetchItemDetails(itemRecord, items) {
        while (itemRecord.next()) {
            var item = {};
            item.name = itemRecord.name.toString();
            item.short_description = itemRecord.description.toString();
            item.picture = itemRecord.icon ? itemRecord.icon.toString() + '.iix' : '';
            item.sys_id = itemRecord.getUniqueValue().toString();

            items.push(item);
        }
    }

    function queryItems(startWindow, endWindow) {
        var grCategories = new GlideRecord(data.tables.SITUATION_CATEGORY);
        grCategories.addActiveQuery();
        grCategories.chooseWindow(startWindow, endWindow);
        grCategories.query();

        return grCategories;
    }
})();
