sp_widget.client_script:
function($scope) {
	/* widget controller */
	var c = this;
	function drawHighchart() {
		if (!$scope.data.ticketCounts) {
			return;
		}
		jQuery("#location-chart").highcharts({
			chart:{
				type:'column'
			},
			title:{
				text:''
			},
			xAxis: {
				labels:
				{
					enabled: false
				}
			},
			yAxis:{
				min: 0,
				title:{
					text:"${total_cases}"
				},
				allowDecimals: false
			},
			plotOptions: {
				column: {
					pointPadding: 0.2,
					borderWidth: 0
				}
			},
			series:[{
				name:$scope.data.incPlural,
				data: $scope.data.ticketCounts.incidents,
				color: '#ff0000'
			},{
				name: $scope.data.nearMissPlural,
				data: $scope.data.ticketCounts.nearMisses,
				color:'#b8860b'
			},{
				name: $scope.data.hazardPlural,
				data: $scope.data.ticketCounts.hazards,
				color:'#551a8b'
			}],
			exporting:{
				enabled:true
			}
		});
	}

	jQuery(document).ready(function(){
		setTimeout(drawHighchart, 500);
	});
}

sp_widget.css:
.case{
  color: red;
  position: absolute;
  width: 10px;
  z-index:25;
}
.circle{
  float:left;
  padding-right: 5px;
  padding-top: 3px;
}

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    // Get the field labels from sys_documentation table

    var nearMissTableName = CIRConstants.Tables.NEAR_MISS;
    var incidentName = CIRConstants.Tables.INCIDENT;
    var hazardTableName = CIRConstants.Tables.HAZARD;
    var element = '';

    var cirCaseManagementUtils = new CIRCaseManagementUtils();

    var nearMissGR = cirCaseManagementUtils.GetFieldLabels(nearMissTableName, element);
    var incGR = cirCaseManagementUtils.GetFieldLabels(incidentName, element);
    var hazardGR = cirCaseManagementUtils.GetFieldLabels(hazardTableName, element);

    data.nearMissLabel = nearMissGR.label.toString();
    data.incLabel = incGR.label.toString();
    data.hazardLabel = hazardGR.label.toString();

    data.nearMissPlural = nearMissGR.plural.toString();
    data.incPlural = incGR.plural.toString();
    data.hazardPlural = hazardGR.plural.toString();

    var locationSys = $sp.getParameter('location_id');
    var locationGR = new GlideRecordSecure(CIRConstants.Tables.LOCATION);
    if(locationGR.get(locationSys)) {
        data.locationImg = locationGR.x_fru_cir_map.getDisplayValue();
        data.caseLocations = new global.JSON().decode(cirCaseManagementUtils.GetCaseLocations('', locationSys, true));
    }
    if(!data.locationImg) {
        data.ticketCounts = cirCaseManagementUtils.GetCasesByTypeForLocation(locationSys);
    }
})();
