sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {
    var layoutFieldGenerator = new global.NotificationLayoutFieldGenerator();
    var identifier = layoutFieldGenerator.layoutField(current.getTableName(), current.sys_id, "number");
	var description = layoutFieldGenerator.layoutField(current.getTableName(), current.sys_id, "short_description");
    var status = layoutFieldGenerator.layoutField(current.getTableName(), current.sys_id, "state");
    var appletDestination='';
	if(current.sys_class_name == 'x_fru_cir_hazard') appletDestination='44622d1adb9adc10d4a73533f3961988';
	else if(current.sys_class_name == 'x_fru_cir_near_miss')	appletDestination='e518615edb9adc10d4a73533f3961982';
	else if(current.sys_class_name == 'x_fru_cir_incident') appletDestination='0cb32d5adb9adc10d4a73533f3961907';

	var deepLinkGenerator = new global.MobileDeepLinkGenerator("Agent");
	var link = deepLinkGenerator.getFormScreenLink(appletDestination, current.getTableName(), current.getValue("sys_id"));

	var json = {};

	json = {
        "aps" : {
            "sound" : "default"
        },
		"Link": link,
        "Layout" : {
            "Status": status,
            "Identifier" : identifier,
            "Description" : description
        }
    };

    return json;

})(current, message, attributes);
