sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var at = ' ' + gs.getMessage('at') + ' ';
    var currentBuildings = "";
    var currentGroups = "";
    var currentVirtualLocations = "";
    var locations = "";

    if (current.getValue('buildings')) {
        currentBuildings = current.getDisplayValue('buildings');
    }

    if (current.getValue('location_groups')) {
        if (current.getValue('buildings')) {
            currentGroups = ", " + current.getDisplayValue('location_groups');
        } else {
            currentGroups = current.getDisplayValue('location_groups');
        }
    }

    if (current.getValue('virtual_locations')) {
        if (current.getValue('location_groups') || current.getValue('buildings')) {
            currentVirtualLocations = ", " + current.getDisplayValue('virtual_locations');
        } else {
            currentVirtualLocations = current.getDisplayValue('virtual_locations');
        }

    }

    locations = currentBuildings + currentGroups + currentVirtualLocations;
	current.situation_location = locations;
    if (locations) {
        current.short_description = current.getDisplayValue("number") + ": " + current.response_plan_template.getDisplayValue() + at + locations;
    } else {
        current.short_description = current.getDisplayValue("number") + ": " + current.response_plan_template.getDisplayValue();
    }

})(current, previous);
