sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var language = gs.getSession().getLanguage() ? gs.getSession().getLanguage() : 'en';
	var messageCountry;
	var country = new GlideRecord('sys_choice');
	country.addEncodedQuery('element=country^name=cmn_location^language=' + language + "^value=" + current.country);
	country.query();
	if(country.next()){
		var address = new GlideRecord(CIRConstants.Tables.SECURITY_ADDRESS_VALUE);
		address.addEncodedQuery('type=country^value=' + country.value);
		address.query();
		if(address.next()){
			messageCountry = address.sys_id;
		}
	}
    var grMessageSet = new GlideRecord(CIRConstants.Tables.MSG_SET);
    grMessageSet.addEncodedQuery('dimension=' + current.dimension + "^situation_categoryLIKE" + current.category + "^countries=" + messageCountry);
    grMessageSet.query();

    if (!grMessageSet.hasNext()) {
        gs.addInfoMessage("No message sets available.");
	} else {
        while (grMessageSet.next()) {
            var param = new GlideRecord(CIRConstants.Tables.MSG_SET_PARAM_M2M);
            param.initialize();
            param.setValue('message_set', grMessageSet.sys_id);
            param.setValue('situation_parametrization', current.getUniqueValue());
            param.insert();
        }
    }

})(current, previous);
