sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var recordTable = CIRConstants.Tables.SITUATION_CATEGORY;
	var nameOfFile = "";
	var message = gs.getMessage('cir_sit_attached_item_situation');

	var getAttachNames = new GlideRecord(CIRConstants.Tables.ATTACHMENT);
	getAttachNames.addQuery('table_sys_id', current.category);
	getAttachNames.addQuery('file_name', '!=', 'icon');
	getAttachNames.query();

	while (getAttachNames.next()){
		nameOfFile = getAttachNames.getValue('file_name');
		getAttachmentRecord(recordTable, nameOfFile);
	}

    /**
     * Get the GlideRecord object for an attachment on a given record.
     * @param {string} recordTable - The table name for the record that has the attachment.
     * @param {string} nameOfFile - The name of the attachment file to retrieve.
     */
    function getAttachmentRecord(recordTable, nameOfFile) {
        var grAttachment = new GlideRecord(CIRConstants.Tables.ATTACHMENT);
        grAttachment.addQuery('table_name', recordTable);
        grAttachment.addQuery('file_name', nameOfFile);
        grAttachment.query();

        if (grAttachment.next()) {
			var getAttachment = new GlideSysAttachment();
			var attachment = getAttachment.getContentBase64(grAttachment);
			getAttachment.writeBase64(current, grAttachment.file_name, grAttachment.content_type, attachment);			
			gs.addInfoMessage(message);
        }
    }
})(current, previous);
