sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var gr = new GlideRecord(CIRConstants.Tables.ON_CALL_TEAM);
    gr.addQuery("sys_id", "!=", current.sys_id);
    gr.addQuery("group", current.group);
    gr.query();

    if(gr.hasNext()){
        gs.addErrorMessage(gs.getMessage("cir_restrictions_on_call_team_group"));
        current.setAbortAction(true);
    }

    var countries = current.country.split(",");

    for (var country = 0; country < countries.length; ++country) {
        gr = new GlideRecord(CIRConstants.Tables.ON_CALL_TEAM);
        gr.addQuery("sys_id", "!=", current.sys_id);
        gr.addQuery("dimension", current.dimension);
        gr.addEncodedQuery("countryLIKE" + countries[country]);
        gr.query();

        if(gr.hasNext()){
            gs.addErrorMessage(gs.getMessage("cir_restrictions_on_call_team_dimension"));
            current.setAbortAction(true);
        }
    }

})(current, previous);
