sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
	var tables = CIRConstants.Tables;
    current.countries = '';
    //type is building
    if (current.group_type == '0') {
        var buildings = current.buildings.split(',');
        current.countries = getCountries(tables.SECURITY_LOCATION, buildings).toString();

    }
    // type is virtual
    if (current.group_type == '1') {
        var systemLocs = current.system_locations.split(',');
        current.countries = getCountries(tables.SECURITY_VIRTUAL_LOCATION, systemLocs).toString();
    }



    function getCountries(table, locations) {
        var countries = [];
        var grLocations = new GlideRecord(table);

        for (var i = 0; i < locations.length; i++) {
            if (grLocations.get(locations[i].toString())) {
                countries.push(grLocations.getValue('country'));
            }
        }
        var arrUtils = new global.ArrayUtil();
        countries = arrUtils.unique(countries);

        if (table === tables.SECURITY_LOCATION) {
            countries = getAddressValCountries(countries);
        }


        return countries.join(',');

    }



    function getAddressValCountries(countries) {
        var addrIds = [];

        for (var i = 0; i < countries.length; i++) {
            var grAddrVal = new GlideRecord(tables.SECURITY_ADDRESS_VALUE);
            grAddrVal.addQuery('type', 'country');
            grAddrVal.addQuery('language', 'en');
            grAddrVal.addQuery('value', countries[i].toString());
            grAddrVal.query();

            if (grAddrVal.next()) {
                addrIds.push(grAddrVal.getUniqueValue());
            }
        }
        return addrIds;
    }


})(current, previous);
