sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
        return;
    }

    var templateField = g_form.getValue('message_template');
    if (!g_form.isNewRecord()) {
        if (oldValue !== newValue) {
            g_form.clearValue('message_type');
        }
    }

    var ga = new GlideAjax('CIRSituationUtilsClient');
    ga.addParam('sysparm_name', 'getMessageTemplate');
    ga.addParam('sysparm_message_template', templateField);
    ga.getXML(updateType);
}

function updateType(response) {
    var usedTemplate = g_form.getValue('message_template').toString();
    var answer = response.responseXML.documentElement.getAttribute('answer');
    var template = JSON.parse(answer);

    if (usedTemplate !== '') {
        if (template) {
            g_form.setValue('message_type', template.type);
            if (template.type === 'email') {
                g_form.setValue('msg_subject', template.subject);
                g_form.setValue('body', template.body);
				g_form.setVisible('body', true);
            } else {
				g_form.setValue('msg_subject', template.name);
                g_form.setValue('message', template.plain);
				g_form.setVisible('message', true);
            }
           // g_form.setReadOnly('message_type', true);
        }
    }
}
