sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var addressVal = {};
	g_form.getReference('inc_location', function(locRef) {
		g_form.setValue('company', locRef.company);
		g_form.setValue('building_type', locRef.type);

		var ga = new GlideAjax('CIRPSIAjaxUtils');
		ga.addParam('sysparm_name', 'getAddressRecs');
		ga.addParam('sysparm_locationId', locRef.sys_id);
		ga.getXML(getData);
	});


	function getData(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var address = JSON.parse(answer);

		if (address.country) {
			g_form.setValue('country', address.country);
		} else {
			g_form.setValue('country', '');
		}

		if (address.state) {
			g_form.setValue('state_province', address.state);
		} else {
			g_form.setValue('state_province', '');
		}

		if (address.city) {
			g_form.setValue('city', address.city);
		} else {
			g_form.setValue('city', '');
		}

	}
}
