sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var building = g_form.getValue('building');
	var currentSpaces = g_form.getValue('space');

	var utils = new GlideAjax('CIRStaticProtectionClientUtils');
	utils.addParam('sysparm_spaceIds', currentSpaces);
	utils.addParam('sysparm_buildingIds', building);
	utils.addParam('sysparm_name', 'filterSpaces');
	utils.getXML(filterSpaces);

	function filterSpaces(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var newSpaces = JSON.parse(answer);

		if(currentSpaces == newSpaces) {
			return;
		}

		g_form.setValue('space', newSpaces);
	} 
}
