sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if(newValue == 'true'){
		g_form.showFieldMsg('individual_not_found', 'It is needed to attach a evidence of the consent of the user to be registered in the system');
	}
	else{
		g_form.hideFieldMsg('individual_not_found');
	}

	g_form.clearValue('employee');
	g_form.clearValue('first_name');
	g_form.clearValue('last_name');
	g_form.clearValue('email');
	g_form.clearValue('phone');
	g_form.clearValue('company');
	g_form.clearValue('address'); 
}
