sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   if (!g_user.hasRole('x_fru_cir.product_recall')) {
        g_form.removeOption('category', 'food_safety');
		g_form.hideRelatedList('x_fru_cir_incident_product_recall');
    }
	else if(g_form.getValue('category')==='food_safety' || g_form.getValue('category')==='injury_illness' ){
		g_form.showRelatedList('x_fru_cir_incident_product_recall');
	}
	else{
		g_form.hideRelatedList('x_fru_cir_incident_product_recall');
	}
}
