sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var sDateValue = g_form.getValue('from_date');
	var sDateFormat = getDateFromFormat(sDateValue, g_user_date_format);
	var sDate = new Date(sDateFormat);

	var eDateValue = g_form.getValue('to_date');
	var eDateFormat = getDateFromFormat(eDateValue, g_user_date_format);
	var eDate = new Date(eDateFormat);

	if(eDate < sDate){
		g_form.addInfoMessage(getMessage('cir_static_invalid_date'));
		g_form.setValue('to_date','');
	}

	if(eDate > sDate){
		g_form.clearMessages();
	}
}
