sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	g_form.clearMessages();
	g_form.setValue('vehicle', '');

	var startDate = g_form.getValue('from_date');
	var endDate = g_form.getValue('to_date');

	var ga = new GlideAjax('CIRDateUtilsAjax');
	ga.addParam("sysparm_name", 'validateDates');
	ga.addParam("sysparm_start_date", startDate);
	ga.addParam("sysparm_end_date", endDate);
	ga.addParam("sysparm_validate_past", 'true');
	ga.getXML(function (response) {
		var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
		if (!answer) {
			g_form.addInfoMessage(getMessage('cir_invalid_date'));
			g_form.setValue('from_date', '');
		}
	}); 
}
