sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var currentBuilding = g_form.getValue('building') ;
	var space = g_form.getValue('space');

	var utils = new GlideAjax('CIRStaticProtectionClientUtils');
	utils.addParam('sysparm_spaceId', space);
	utils.addParam('sysparm_name', 'getParentBuilding');
	utils.getXML(getBuilding);

	function getBuilding(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var newBuilding = JSON.parse(answer);

		if(!newBuilding || currentBuilding.indexOf(newBuilding+'') != -1) {
			return;
		}

		newBuilding = currentBuilding + ',' + JSON.parse(answer);
		g_form.setValue('building',newBuilding.toString());
	}
}
