sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var ga = new GlideAjax('CIRCaseManagementAjax');
    ga.addParam('sysparm_name', 'getPotentialSeverity');
    ga.addParam('sysparm_futureLikelihood', g_form.getValue('future_likelihood'));
    ga.addParam('sysparm_potentialConsequence', g_form.getValue('potential_consequence'));
    ga.getXMLAnswer(function(answer) {
        if (answer) {
            g_form.setValue('potential_severity', answer);
        }
    });
   //Type appropriate comment here, and begin script below

}
