sys_script_client.script:
function onSubmit() {
    var isValid = true;

    if (g_form.getValue('buildings').length > 32) {
        g_form.addErrorMessage(getMessage("cir_response_execution_buildings"));
        isValid = false;
    } else if (g_form.getValue('location_groups').length > 32) {
        g_form.addErrorMessage(getMessage("cir_response_execution_location_groups"));
        isValid = false;
    } else if (g_form.getValue('virtual_locations').length > 32) {
        g_form.addErrorMessage(getMessage("cir_response_execution_virtual_locations"));
        isValid = false;
    }

    return isValid;
}
