sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Determine if the field is a number
	if (isNaN(newValue)) {
		g_form.setValue('year', new Date().getFullYear());
		g_form.showErrorBox('year', 'You may only introduce number values');

	} else if (newValue.length > 4 || newValue.length < 4 || newValue.length != 4) {
		//Determine if the field is greater or lower than four digits and prevent that from occurring
		g_form.setValue('year', new Date().getFullYear());
		g_form.showErrorBox('year', 'That is not a valid year');
		return;
	}

	//Determine if the value is lower than 0 and prevent that from occurring
	if (newValue < 2000 || newValue > 2999) {
		g_form.setValue('year', new Date().getFullYear());
		g_form.showErrorBox('year', 'You may only introduce a year between 2000 and 2999');
		return;
	}
}
