sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {
        return;
    }

    var vehicle = g_form.getValue('affected_vehicle');

    if (vehicle) {
        var ga = new GlideAjax('CIRPSIAjaxUtils');
		ga.addParam('sysparm_name', 'getVehicleInfo');
        ga.addParam('sysparm_vehicle', vehicle);
        ga.getXML(getVehicleData);
    } 

	if(!vehicle){
        g_form.clearValue('driver');
        g_form.clearValue('make');
        g_form.clearValue('model');
        g_form.clearValue('year');
        g_form.clearValue('current_mileage');
        g_form.clearValue('plate_number');
    }

    function getVehicleData(response) {
        var vehicleAnswer = response.responseXML.documentElement.getAttribute("answer");
        var vehicleData = JSON.parse(vehicleAnswer);
        g_form.setValue('driver', vehicleData.driver);
        g_form.setValue('make', vehicleData.make);
        g_form.setValue('model', vehicleData.model);
        g_form.setValue('year', vehicleData.year);
        g_form.setValue('current_mileage', vehicleData.current_mileage);
        g_form.setValue('plate_number', vehicleData.plate_number);
    }
}
