sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		g_form.setReadOnly('building',false);
		return;
	}

	g_form.setReadOnly('building',true);

	var utils = new GlideAjax('CIRStaticProtectionClientUtils');
	utils.addParam('sysparm_spaceId', g_form.getValue('space'));
	utils.addParam('sysparm_name', 'getParentBuilding');
	utils.getXML(callBack);

	function callBack(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var parentBuilding = JSON.parse(answer);

		if(parentBuilding) {
			g_form.setValue('building', parentBuilding);
		}
	}
}
