sys_script_client.script:
function onLoad() {
	g_form.getReference('message_set', setChoices);

	function setChoices(msgSet) {
		if (msgSet.table) {
			switch (msgSet.table) {
				case 'x_fru_cir_situation':
					g_form.removeOption('dynamic_contact', 'assignee_of_situation_task');
					g_form.removeOption('dynamic_contact', 'member_added');
					g_form.removeOption('dynamic_contact', 'response_plan_coordinator');
					g_form.removeOption('dynamic_contact', 'response_plan_team');
					break;
				case 'x_fru_cir_situation_member':
					if (msgSet.on_insert == 'false') {
						g_form.removeOption('contact_option', 'dynamic');
						g_form.setValue('contact_option', 'manual');
						g_form.setReadOnly('contact_option', true);
						g_form.showFieldMsg('contact_option', getMessage('cir_situation_message_set_table_on_insert_info'));
					}
					g_form.removeOption('dynamic_contact', 'assignee_of_situation_task');
					g_form.removeOption('dynamic_contact', 'response_plan_coordinator');
					g_form.removeOption('dynamic_contact', 'response_plan_team');
					g_form.removeOption('dynamic_contact', 'situation_members');
					g_form.removeOption('dynamic_contact', 'escalation_team');
					g_form.removeOption('dynamic_contact', 'on_call_contact');
					break;
				case 'x_fru_cir_response_plan_execution':
					g_form.removeOption('dynamic_contact', 'assignee_of_situation_task');
					g_form.removeOption('dynamic_contact', 'member_added');
					g_form.removeOption('dynamic_contact', 'situation_members');
					g_form.removeOption('dynamic_contact', 'escalation_team');
					g_form.removeOption('dynamic_contact', 'on_call_contact');
					break;
				case 'x_fru_cir_response_team':
					if(msgSet.on_insert == 'false') {
						g_form.removeOption('contact_option', 'dynamic');
						g_form.setValue('contact_option', 'manual');
						g_form.setReadOnly('contact_option', true);
						g_form.showFieldMsg('contact_option', getMessage('cir_situation_message_set_table_on_insert_info'));
					}
					g_form.removeOption('dynamic_contact', 'response_plan_team');
					g_form.removeOption('dynamic_contact', 'assignee_of_situation_task');
					g_form.removeOption('dynamic_contact', 'response_plan_ coordinator');
					g_form.removeOption('dynamic_contact', 'crisis_members');
					g_form.removeOption('dynamic_contact', 'situation_members');
					g_form.removeOption('dynamic_contact', 'on_call_contact');
					break;
				case 'x_fru_cir_situation_task_execution':
					g_form.removeOption('dynamic_contact', 'member_added');
					g_form.removeOption('dynamic_contact', 'response_plan_coordinator');
					g_form.removeOption('dynamic_contact', 'response_plan_team');
					g_form.removeOption('dynamic_contact', 'situation_members');
					g_form.removeOption('dynamic_contact', 'escalation_team');
					g_form.removeOption('dynamic_contact', 'on_call_contact');
					break;
			}
		}
	}
}
