sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	if (newValue === '') {
		g_form.setValue('converted_cost', '');
		return;
	}

	newValue = newValue.replace(',','.');

	var cost = parseFloat(newValue);
	cost = cost === 0 ? '0.00' : cost;  

	if (!cost) {
		g_form.showErrorBox('cost_of_incident', getMessage("The value is not valid"));
	} else {
		g_form.getReference('local_currency', getCurrency);
	}

	function getCurrency(currency) {
		var ga = new GlideAjax('CIRPSIAjaxUtils');
		ga.addParam('sysparm_name', 'calculateCost');	
		ga.addParam('sysparm_value', cost);
		ga.addParam('sysparm_currency', currency.code);
		ga.getXML(getIt);
	}

	function getIt(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('converted_cost', answer);
	}
}
