sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (!g_form.getValue('building_name') || !g_form.getValue('space') || isLoading) {
		return;
	}

	if (oldValue !== newValue) {
		var ga = new GlideAjax('CIRPSIAjaxUtils');
		ga.addParam('sysparm_name', 'getFsmLocSpace');
		ga.addParam('sysparm_spaceId', g_form.getValue('space'));
		ga.addParam('sysparm_building', g_form.getValue('building_name'));
		ga.addParam('sysparm_floor', g_form.getValue('floor'));
		ga.addParam('sysparm_fromRp', true);
		ga.getXML(getData);
	}

	function getData(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");

		if (answer && answer !== g_form.getValue('space')) {
			g_form.setValue('space', answer);

		}
	}

}
