sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	var service = g_form.getValue('service');
	var servStart = g_form.getValue('start_date');
	var agenda = '';

	var utils = new GlideAjax('CIRPersonalProtectionClientUtils');
	utils.addParam('sysparm_servStart', servStart);
	utils.addParam('sysparm_name', 'getAgenda');
	utils.addParam('sysparm_service', service);
	utils.getXML(getAgenda);

	function getAgenda(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		agenda = JSON.parse(answer);

		if(agenda){
			g_form.setValue('service_details', agenda.toString());
		}
		else {
			g_form.setValue('service_details','');
			g_form.setValue('service_details.agenda','');
		}
	}
	return;
}
