sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var ga = new GlideAjax('CIRCaseManagementAjax');
	ga.addParam('sysparm_name', 'getUserInformation');
	ga.addParam('sysparm_user', newValue);
	ga.getXMLAnswer(function(answer){
		var user = JSON.parse(answer);
		if(user){
			g_form.setValue('first_name', user.first_name);
			g_form.setValue('last_name', user.last_name);
			g_form.setValue('email', user.email);
			g_form.setValue('phone', user.phone);
			g_form.setValue('company', user.company);
			g_form.setValue('address', user.address);
		}
	});
}
