sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}
	if (newValue == '') {
		g_form.setValue('message_template', '');
		g_form.setValue('message_type', '');			
		g_form.setValue('contact_list', '');
		g_form.setValue('name', '');
		g_form.setValue('description', '');
		g_form.setValue('assigned_to', '');
		g_form.setValue('contact_list', '');
		g_form.setValue('body', '');
		g_form.setValue('message', '');
		g_form.setValue('msg_subject', '');
		return;
	}


	var ga = new GlideAjax('CIRSituationUtilsClient');
	ga.addParam('sysparm_name', 'getTaskTemplate');
	ga.addParam('sysparm_task_template', g_form.getValue('template_name').toString());
	ga.getXML(updateTask);
}


function updateTask(response) {
	var answer = response.responseXML.documentElement.getAttribute('answer');
	var template = JSON.parse(answer);

	if (template) {
		g_form.setValue('name', template.name);
		g_form.setValue('description', template.description);
		g_form.setValue('assigned_to', template.assignee);
		g_form.setValue('state', 2);

		if (template.type === 'message') {
			g_form.setValue('message_template', template.message_template);	
			g_form.setValue('contact_list', template.contacts);
		}
	} 
}
