sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    if (g_form.isNewRecord()) {
        var addseconds = 2; //The amount of time to add in seconds. 43220 = 12 hours
        var ajax = new GlideAjax('CIRDateUtilsAjax');
        ajax.addParam('sysparm_name', 'addNowDateTime');
        ajax.addParam('sysparm_time', addseconds);
        ajax.getXML(dateTime);
	}
	function dateTime(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");
            var result = JSON.parse(answer);
            g_form.setValue('occurred_on', result.time);
            g_form.setValue('end_time', result.timeplus);
        }
}
