sys_script_client.script:
function onLoad() {
   // set country/dimension/default fields read only
	if (g_scratchpad.parametrization) {
		g_form.setMandatory('country', false);
		g_form.setReadOnly('country', true);
		g_form.setReadOnly('default', true);
	}

	// checks the user role to make the field country read only and set the value of the country with the country of the user
	if (!g_user.hasRole('x_fru_cir.situation_administrator') && !g_user.hasRole('x_fru_cir.senior_situation_manager') && g_user.hasRole('x_fru_cir.situation_manager')) {
		g_form.setMandatory('country', false);
		g_form.setReadOnly('country', true);
	}   
}
