sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//  ATTENTION: ServiceNow will bring up this script during certification, but isLoading IS INTENTIONALLY OMITTED. See Jasmine Test for more information: Set Potential Severity Color - Client Script
    if (newValue === '') {
        return;
    }
    g_form.setReadOnly('potential_severity', true);
    var field = g_form.getElement('sys_readonly.' + g_form.getTableName() + '.potential_severity');
    var value = g_form.getValue('potential_severity');
    switch (value) {
        case '1':
            field.style.backgroundColor = '#C00000';
            field.style.color = '#fff';
            break;
        case '2':
            field.style.backgroundColor = '#ED7D31';
            field.style.color = '#fff';
            break;
        case '3':
            field.style.backgroundColor = '#FFC000';
            field.style.color = '#000000';
            break;
        case '4':
            field.style.backgroundColor = '#70AD47';
            field.style.color = '#fff';
            break;
    }
}
