sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var parametrizations = [];
	var buildingsArray = [];
	var virtualLArray = [];
	var encodedQuery = '';

	var buildings = current.getValue('buildings');
	if (buildings) {
		buildingsArray = buildingsArray.concat(buildings.split(','));
	}

	var virtualLocations = current.getValue('virtual_locations');
	if (virtualLocations) {
		virtualLArray = virtualLArray.concat(virtualLocations.split(','));
	}

	var locationGroups = current.getValue('location_groups');
	if (locationGroups) {
		locationGroups = locationGroups.split(',');
		var grLocGroups = new GlideRecord(CIRConstants.Tables.LOCATION_GROUP);
		grLocGroups.addQuery('sys_id', 'IN', locationGroups);
		grLocGroups.query();
		while (grLocGroups.next()) {
			if (grLocGroups.getValue('group_type') == '1') {
				if (grLocGroups.getValue('system_locations')) {
					var locGroupsVirt = grLocGroups.getValue('system_locations').split(',');
					virtualLArray = virtualLArray.concat(locGroupsVirt);
					virtualLArray = new global.ArrayUtil().unique(virtualLArray);
				}
			} else {
				if (grLocGroups.getValue('buildings')) {
					var locGroupBuilds = grLocGroups.getValue('buildings').split(',');
					buildingsArray = buildingsArray.concat(locGroupBuilds);
					buildingsArray = new global.ArrayUtil().unique(buildingsArray);
				}
			}
		}

		// create encoded query for parametrization
		locationGroups = locationGroups.map(function (locationGroup) {
			return 'location_groupsCONTAINS' + locationGroup;
		});
		encodedQuery = locationGroups.join('^OR');
	}

	if (buildingsArray.length != 0) {
		buildingsArray = buildingsArray.map(function (building) {
			return 'buildingsCONTAINS' + building;
		});
		if (encodedQuery) {
			encodedQuery += '^OR' + buildingsArray.join('^OR');
		} else {
			encodedQuery = buildingsArray.join('^OR');
		}
	}
	if (virtualLArray.length != 0) {
		virtualLArray = virtualLArray.map(function (virtualLocation) {
			return 'virtual_locationsCONTAINS' + virtualLocation;
		});
		if (encodedQuery) {
			encodedQuery += '^OR' + virtualLArray.join('^OR');
		} else {
			encodedQuery = virtualLocations.join('^OR');
		}
	}

	var countries = current.getDisplayValue('country');
	var grParam = new GlideRecord(CIRConstants.Tables.PARAMETRIZATION);
	grParam.addQuery('dimension', current.getValue('dimension'));
//	grParam.addQuery('country', 'IN', current.getDisplayValue('country')); // it is not possible to compare diplay values with internal values (it is always in english)
	grParam.addQuery('category', current.getValue('category'));
	grParam.addEncodedQuery(encodedQuery);
	grParam.query();
	while (grParam.next()) {
		if (countries && countries.indexOf(grParam.getDisplayValue('country')) != -1) {
			parametrizations.push(grParam.getUniqueValue());
		}
	}
	current.setValue('parametrization', parametrizations + '');
})(current, previous);
