sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
	var body = '';
	if(current.message_type == 'email'){
		body = current.body.toString();
	} else {
		body = current.message.toString();
	}


	var count = 0;


	if(body.indexOf("#{") != -1) {
		while(body.indexOf("#{") != -1 && count<50){
			var occ = body.match("\#{(.*?)\}")[1];

			body = body.replace(occ, current.getDisplayValue(occ));
			body = body.replace('#{', '');
			body = body.replace('}', '');
			count++;
		}
	}

	template.print(body);


function manageBcc(recipientIDs){

	var grUser = new GlideRecord("sys_user");
	grUser.addEncodedQuery("sys_idIN" + recipientIDs);
	grUser.addQuery("email", "!=", "");
	grUser.query();

	while(grUser.next()) {
		email.addAddress("bcc", grUser.email.toString());
	}
}


})(current, template, email, email_action, event);
