sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var investigation = new GlideRecord(CIRConstants.Tables.INVESTIGATION);
	investigation.addQuery('case', current.sys_id);
	investigation.query();
	if(investigation.next()){
		template.print('<a href="https://' + gs.getProperty('instance_name') + '.service-now.com/' + CIRConstants.Tables.INVESTIGATION + '.do?sys_id=' + investigation.sys_id.toString() + '">' + investigation.number.toString() + '</a>');
	}

})(current, template, email, email_action, event);
