sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {


    //this script will check if the feedback field, in the scenario step message, is checked
    //if so, it will send two buttons on the email body, so the user can give feedback easily
    manageBcc(event.parm1.toString());
    email.addAddress("to", event.parm2.toString());

    if (current.response_plan_step.feedback == true) {
        template.print(CIRSituationUtils().RenderMailtoButton('Ok') + ' ');
        template.print(CIRSituationUtils().RenderMailtoButton('Not Ok'));
    }

    function manageBcc(recipientIDs) {

        var grUser = new GlideRecord("sys_user");
        grUser.addEncodedQuery("sys_idIN" + recipientIDs);
        grUser.addQuery("email", "!=", "");
        grUser.query();

        while (grUser.next()) {
            email.addAddress("bcc", grUser.email.toString());
        }
    }


})(current, template, email, email_action, event);
