sys_script_include.script:
var CIRFleetAccessManagerFP = (function() {
	var CIRFleetAccessManagerFP = Class.create();
	var initialize = function() {};

	/**
	 * Function called in ACLs for tables realted to the Fleet Management process
	 * @param {String} access - access level to check
	 * @return {boolean} - true if the user has access false if it doesn't
	 */ 
	var checkAccess = function () {
		var table = current.getTableName();

		switch (table) {
			case CIRConstants.Tables.DRIVER_REPORT:
				return getDriverReportAccess();
		}
	};

	/**
	 * Function called checkAccess that will chekc the access for the Driver report table;
	 * @return {boolean} - true if the user has access false if it doesn't
	 */ 
	var getDriverReportAccess = function () {
		gs.info('HERE');
		var bgProfile = new CIRFleetUtils().GetProtectionProfile(gs.getUserID());
		var bgList = current.bodyguards.toString();

		if(current.user == gs.getUserID() || bgList.indexOf(bgProfile) != -1) {
			return true;
		} return false;
	};

	CIRFleetAccessManagerFP.prototype = {
		initialize: initialize,
		CheckAccess : checkAccess
	};

	return CIRFleetAccessManagerFP;
})();
