sys_script_include.script:
var CIRSecurityLocationAdressCopy = function() {
	var tables = CIRConstants.Tables;

	var copyAddresFromFsm = function() {
		var fields = ['state', 'country', 'city', 'name'];
		var sysLanguage = gs.getSession().getLanguage();
		this.copyFields(fields, sysLanguage);

		fields = ['country'];
		sysLanguage = 'es';
		copyFields(fields, sysLanguage);
	};

	var copyFields =  function(fields, language) {

		var facilityType = '';
		var grLoc = new GlideRecord(tables.SECURITY_LOCATION);
		grLoc.query();

		while (grLoc.next()) {

			for (var i = 0; i < fields.length; i++) {
				if (fields[i] != 'name') {
					createAddrValue(fields[i], grLoc.getDisplayValue(fields[i]), language);
				} else if(grLoc.getDisplayValue('type')){
					facilityType = grLoc.getDisplayValue('type');
					createAddrValue(facilityType, grLoc.getDisplayValue(fields[i]), language);
				}
			}
		}
	};

	var createAddrValue = function (type, value, language) {

		if (!value) {
			return;
		}

		var addrValGr = new GlideRecord(tables.SECURITY_ADDRESS_VALUE);
		addrValGr.addQuery('language', language);
		addrValGr.addQuery('value', value);
		addrValGr.addQuery('type', type);
		addrValGr.query();

		if (addrValGr.getRowCount() < 1) {

			var newAddrVal = new GlideRecord(tables.SECURITY_ADDRESS_VALUE);
			newAddrVal.initialize();
			newAddrVal.setValue('language', language);
			newAddrVal.setValue('value', value);
			newAddrVal.setValue('type', type);
			newAddrVal.insert();
		}
	};

	return {
		CopyAddresFromFsm: copyAddresFromFsm,
		CopyFields: copyFields,
		CreateAddrValue: createAddrValue
	};
};
