sys_script_include.script:
var CIRDateUtilsAjax = Class.create();
CIRDateUtilsAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	validateDates :  function() {
		var startDate = this.getParameter('sysparm_start_date');
		var endDate = this.getParameter('sysparm_end_date');
		var validatePast = this.getParameter('sysparm_validate_past') == 'true';
		var obj = new CIRDateUtils().ValidateDates(startDate, endDate, validatePast);
		return JSON.stringify(obj);
	},

	addNowDateTime: function(){
		var now = new GlideDateTime();
		var nowplus = new GlideDateTime();
		var time = this.getParameter('sysparm_time'); 
		nowplus.addSeconds(time);
		var obj = {};
			obj.time = now.getDisplayValueInternal();
			obj.timeplus = nowplus.getDisplayValueInternal();
		return JSON.stringify(obj);
	},

	type: 'CIRDateUtilsAjax'
});
