sys_script_include.script:
var CIRRiskManagementAjaxUtils = Class.create();
CIRRiskManagementAjaxUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	/**
     * Get the sys_id of the contact person on the Equipment record
	 *
     * @param {String} componentId - sys_id of Contributing Factor
     * @return {String} - sys_id of the contact person
     */
	getContactPerson: function() {
		var componentId = this.getParameter('sysparm_componentId');
		return new CIRRiskManagementUtils().GetContactPerson(componentId);
	},

    type: 'CIRRiskManagementAjaxUtils'
});
