sys_script_include.script:
var CIRSituationFilterTables = Class.create();
CIRSituationFilterTables.prototype = {
	initialize: function() {
	},

	/*
     *
     * Used to filter out the possible tables to select on message sets
     */
	process: function(){

		var tablesConst = CIRConstants.Tables;
		var tables = [tablesConst.SITUATION, tablesConst.RESP_PLAN_EXEC, tablesConst.TASK_EXECUTION, tablesConst.SITUATION_MEMBER, tablesConst.RESPONSE_TEAM];
		return tables;

	},

	type: 'filterTables'
};
